mapper.update:
update t_fullpull_history
type = #{type,jdbcType=VARCHAR}, dsName = #{dsName,jdbcType=VARCHAR}, schemaName = #{schemaName,jdbcType=VARCHAR}, tableName = #{tableName,jdbcType=VARCHAR}, version = #{version,jdbcType=INTEGER}, batch_id = #{batchId,jdbcType=INTEGER}, state = #{state,jdbcType=VARCHAR}, error_msg = #{errorMsg,jdbcType=VARCHAR}, init_time = #{initTime,jdbcType=TIMESTAMP}, start_split_time = #{startSplitTime,jdbcType=TIMESTAMP}, start_pull_time = #{startPullTime,jdbcType=TIMESTAMP}, end_time = #{endTime,jdbcType=TIMESTAMP}, update_time = #{updateTime,jdbcType=TIMESTAMP}, finished_partition_count = #{finishedPartitionCount,jdbcType=BIGINT}, total_partition_count = #{totalPartitionCount,jdbcType=BIGINT}, finished_row_count = #{finishedRowCount,jdbcType=BIGINT}, total_row_count = #{totalRowCount,jdbcType=BIGINT}, project_name = #{projectName,jdbcType=VARCHAR}, topology_table_id = #{topologyTableId,jdbcType=INTEGER}, target_sink_id = #{targetSinkId,jdbcType=INTEGER}, target_sink_topic = #{targetSinkTopic,jdbcType=VARCHAR}, full_pull_req_msg_offset = #{fullPullReqMsgOffset,jdbcType=BIGINT}, first_shard_msg_offset = #{firstShardMsgOffset,jdbcType=BIGINT}, last_shard_msg_offset = #{lastShardMsgOffset,jdbcType=BIGINT}, split_column = #{splitColumn,jdbcType=VARCHAR}, fullpull_condition = #{fullpullCondition,jdbcType=INTEGER},
where id = #{id,jdbcType=BIGINT}

mapper.select:
select t_sink.sink_name as target_sink_name, pro.project_display_name, fh.* from t_fullpull_history fh
join t_project_topo_table tt on fh.topology_table_id = tt.id join t_project_user pu on tt.project_id =pu.project_id and pu.user_id = #{userId}
left join t_project pro on fh.project_name = pro.project_name left join t_sink on fh.target_sink_id = t_sink.id where 1=1 and fh.project_name = #{projectName} and fh.dsName = #{dsName} and fh.schemaName like "%"#{schemaName}"%" and fh.tableName like "%"#{tableName}"%" and fh.target_sink_topic like "%"#{targetSinkTopic}"%" and fh.id = #{id} order by ${orderBy} desc
