mapper.select:
select id, project_id, table_id, fullpull_enable_flag, update_time
from t_project_resource
where id = #{id,jdbcType=INTEGER}

select * from (select res.*, IF (tdec.table_id, 1, 0) hasDbaEncode from ( SELECT DISTINCT * FROM ( SELECT dsource.ds_type dsType, dsource.ds_name dsName, dtable.id tableId, project.id projectId, dschema.schema_name schemaName, dtable.table_name tableName, dtable.status, dtable.description, presource.fullpull_enable_flag ifFullpull FROM t_dbus_datasource dsource, t_data_schema dschema, t_data_tables dtable, t_project_resource presource, t_project project
dtable.id=presource.table_id AND presource.project_id=project.id AND dtable.ds_id = dsource.id AND dtable.schema_id = dschema.id AND dsource.ds_name like "%"#{dsName}"%" AND dschema.schema_name like "%"#{schemaName}"%" AND dtable.table_name like "%"#{tableName}"%" AND presource.project_id=#{projectId}
) tresource WHERE tresource.tableId not in( SELECT DISTINCT table_id FROM t_project_topo_table WHERE project_id = #{projectId} AND topo_id = #{topoId} ) ) res LEFT JOIN ( SELECT DISTINCT table_id FROM t_dba_encode_columns GROUP BY table_id ) tdec ON res.tableId = tdec.table_id) t
t.hasDbaEncode=#{hasDbaEncode}

select *
from t_project_resource
where project_id = #{projectId,jdbcType=INTEGER} AND table_id = #{tableId,jdbcType=INTEGER}
